@import url('https://fonts.googleapis.com/css2?family=Montserrat:wght@400;600;700;800&family=Inter:wght@400;500;600&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(210, 25%, 7.8431%);
  --card: hsl(180, 6.6667%, 97.0588%);
  --card-foreground: hsl(210, 25%, 7.8431%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(210, 25%, 7.8431%);
  --primary: hsl(196, 30%, 58%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(210, 25%, 7.8431%);
  --secondary-foreground: hsl(0, 0%, 100%);
  --muted: hsl(240, 1.9608%, 90%);
  --muted-foreground: hsl(210, 25%, 7.8431%);
  --accent: hsl(211.5789, 51.3514%, 92.7451%);
  --accent-foreground: hsl(196, 30%, 58%);
  --destructive: hsl(356.3033, 90.5579%, 54.3137%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(201.4286, 30.4348%, 90.9804%);
  --input: hsl(200, 23.0769%, 97.4510%);
  --ring: hsl(196, 30%, 58%);
  --radius: 1.3rem;

  /* Brand Colors */
  --brand-red: hsl(351, 55%, 45%);
  --metallic: hsl(0, 0%, 74%);
  --dark-gray: hsl(0, 0%, 20%);
}

.dark {
  --background: hsl(0, 0%, 0%);
  --foreground: hsl(200, 6.6667%, 91.1765%);
  --card: hsl(228, 9.8039%, 10%);
  --card-foreground: hsl(0, 0%, 85.0980%);
  --popover: hsl(0, 0%, 0%);
  --popover-foreground: hsl(200, 6.6667%, 91.1765%);
  --primary: hsl(196, 30%, 58%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(195, 15.3846%, 94.9020%);
  --secondary-foreground: hsl(210, 25%, 7.8431%);
  --muted: hsl(0, 0%, 9.4118%);
  --muted-foreground: hsl(210, 3.3898%, 46.2745%);
  --accent: hsl(205.7143, 70%, 7.8431%);
  --accent-foreground: hsl(196, 30%, 58%);
  --destructive: hsl(356.3033, 90.5579%, 54.3137%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(210, 5.2632%, 14.9020%);
  --input: hsl(207.6923, 27.6596%, 18.4314%);
  --ring: hsl(196, 30%, 58%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-inter antialiased bg-background text-foreground;
  }
}

/* Custom animations and effects */
@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

@keyframes slideUp {
  from { transform: translateY(30px); opacity: 0; }
  to { transform: translateY(0); opacity: 1; }
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes marquee {
  0% { transform: translateX(100%); }
  100% { transform: translateX(-100%); }
}

@keyframes pulse {
  0% { transform: scale(1); }
  50% { transform: scale(1.04); }
  100% { transform: scale(1); }
}

.animate-float {
  animation: float 3s ease-in-out infinite;
}

.animate-slideUp {
  animation: slideUp 0.6s ease-out;
}

.animate-fadeIn {
  animation: fadeIn 0.8s ease-out;
}

.animate-marquee {
  animation: marquee 20s linear infinite;
}

.animate-pulse-scale {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.animate-pulse-scale:hover {
  animation: pulse 0.3s ease-in-out;
}

/* Hero gradient */
.hero-gradient {
  background: linear-gradient(135deg, rgba(0,0,0,0.8) 0%, rgba(196,30,58,0.3) 100%);
}

/* Text shadow for hero */
.text-shadow {
  text-shadow: 2px 2px 4px rgba(0,0,0,0.8);
}

/* Glass effect */
.glass-effect {
  backdrop-filter: blur(10px);
  background: rgba(255,255,255,0.1);
}

/* Hover lift effect */
.hover-lift {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.hover-lift:hover {
  transform: translateY(-8px);
  box-shadow: 0 20px 40px rgba(0,0,0,0.2);
}

/* Before/After slider styles */
.before-after-container {
  position: relative;
  overflow: hidden;
}

.before-after-slider {
  position: absolute;
  left: 50%;
  top: 0;
  bottom: 0;
  width: 4px;
  background: var(--brand-red);
  cursor: ew-resize;
  z-index: 10;
}

.before-after-handle {
  position: absolute;
  top: 50%;
  left: -15px;
  width: 34px;
  height: 34px;
  background: var(--brand-red);
  border: 3px solid white;
  border-radius: 50%;
  transform: translateY(-50%);
  cursor: ew-resize;
}

/* Mobile call bar */
.mobile-call-bar {
  background: linear-gradient(90deg, var(--brand-red) 0%, hsl(351, 55%, 40%) 100%);
}

/* Marquee container */
.marquee-container {
  overflow: hidden;
  white-space: nowrap;
}

.marquee-content {
  display: inline-block;
  animation: marquee 30s linear infinite;
}

.marquee-content:hover {
  animation-play-state: paused;
}

/* Responsive design adjustments */
@media (prefers-reduced-motion: reduce) {
  .marquee-content,
  .animate-float,
  .animate-pulse-scale:hover,
  .hover-lift:hover {
    animation: none;
    transform: none;
  }
}

/* Brand color utilities */
.text-brand-red {
  color: var(--brand-red);
}

.bg-brand-red {
  background-color: var(--brand-red);
}

.border-brand-red {
  border-color: var(--brand-red);
}

.text-metallic {
  color: var(--metallic);
}

.bg-metallic {
  background-color: var(--metallic);
}

.text-dark-gray {
  color: var(--dark-gray);
}

.bg-dark-gray {
  background-color: var(--dark-gray);
}

